mapper.select:
SELECT
m.user_name as userName,
m.order_type as orderType,
m.money AS money,
m.before_money as beforeMoney,
m.after_money as afterMoney,
m.create_time as createTime
FROM
kpn_money_log m
LEFT JOIN sys_user u ON u.id = m.user_id


		and m.user_name = #{r.userName}


		and m.order_no = #{r.orderNo}


		and u.site_id = #{r.siteId}


		AND  m.create_time >= #{r.startTime}



		AND  m.create_time <= #{r.endTime}

SELECT count(1) AS total_number, sum(ifnull(money, 0)) as money FROM kpn_money_log m LEFT JOIN sys_user u ON u.id = m.user_id
