mapper.select:
select t.* from sys_user u left join mks_quiz_orders t on  t.member_id=u.id

    1=1

        n.number in

            #{item}




        and u.parent_name = #{p.parentName}


        and t.site_code = #{p.siteCode}


        and t.status = #{p.status}



        and t.periods = #{p.periods}


        and t.site_lottery_id = #{p.siteLotteryId}


        and to_days(t.create_time) = to_days(now())


        and to_days(now())-to_days(t.create_time) =1


        and to_days(now())-to_days(t.create_time)  <=  7

    ORDER BY t.createTime

            ASC 
             DESC

SELECT u.username, m.order_no, m.order_type, COALESCE(abs(m.money),0) as income, COALESCE(m.after_money,0) as balance, m.create_time from sys_user u left join mks_money_log m on u.username=m.user_name
u.site_id=#{p.siteId} and m.create_time between #{p.startTime} and #{p.endTime}
m.user_name=#{p.username}
ORDER BY m.create_time asc

SELECT o.user_name, o.lottery_name, CONCAT( o.site_category_name, ',', o.quiz_title, ',', o.quiz_details_name ) AS site_category_name, o.periods, m.numbers, o.quiz_introduces, o.total_price, o.units, o.win_mount, o.`status`, o.create_time, o.order_no FROM mks_quiz_orders o left join mks_wn_data m on o.periods=m.qihao and o.lottery_id=m.lottery_id
o.site_id=#{p.siteId} and o.create_time between #{p.startTime} and #{p.endTime}
m.user_name=#{p.username}
ORDER BY m.create_time asc
